let CMPContract;
let WETHContract;
let USDTContract;
let Value;
let Value1;


export const init=async()=>{
    provider = window.ethereum;

    if(typeof provider!=='undefined'){
      
     await provider
      .request({method:'eth_requestAccounts'})
      .then(accounts=>{
          selectedAccount = accounts[0];
          console.log(`Connected to ${ selectedAccount} account`);})
      .catch(err=>{
        console.log(err);
      });
      window.ethereum.on('accountsChanged',function(accounts){
        selectedAccount = accounts[0];
        console.log(`Changed to ${ selectedAccount} account`)
      });
    }    
    web3 = new Web3(provider);
    DEXContract = new web3.eth.Contract(uniswapAbi,uniswapContractAddress);
	console.log(`Contract 1 loaded`);
    CMPContract = new web3.eth.Contract(CMPAbi,CMPAddress);
	console.log(`Contract 2 loaded`);
	WETHContract = new web3.eth.Contract(WETHAbi,tokenOutWeth);
    console.log(`Contract 3 loaded`);
};
export const wethAdd=async()=>{
  const wethAddressFromFunction =  await DEXContract.methods.weth().call();
  console.log(wethAddressFromFunction);
};
export const getOutputAmount=async(_tokenAmount,_tokenIn,_tokenOut)=>{
  Value1 =  web3.utils.toWei(_tokenAmount, 'ether');
  Value = Value1.toString();
  return(await DEXContract.methods.getQuote(Value,_tokenIn,_tokenOut).call());
  
};
export const exactTokenForEth=async(_tokenAmount,_tokenIn,approved)=>{
	Value1 =  web3.utils.toWei(_tokenAmount, 'ether');
	Value = Value1.toString();
		switch (approved) {
		default:console.log("Invalid Token");
			break;
		case 'cmp': 
			await CMPContract.methods.approve(uniswapContractAddress, Value1).send({from:selectedAccount});
			await DEXContract.methods.swapTokensForEth(Value,0,_tokenIn,selectedAccount).send({from:selectedAccount});
		break;
	
		case 'weth':
			await WETHContract.methods.approve(uniswapContractAddress, Value).send({from:selectedAccount});
			await DEXContract.methods.swapTokensForEth(Value,0,_tokenIn,selectedAccount).send({from:selectedAccount});
		break;
	  }

	  console.log("Transaction complete");
	}

export const exactEthForToken=async(_tokenOut,_tokenAmount)=>{
	Value1 =  web3.utils.toWei('1', 'ether');
	Value = Value1.toString();
	await DEXContract.methods.swapEthForTokens(0,_tokenOut,selectedAccount).send({from:selectedAccount,value:Value});
	console.log("Transaction complete");
  };

export const exactTokenForToken=async(_tokenIn,_tokenOut,_tokenAmount,approved)=>{
	Value1 =  web3.utils.toWei(_tokenAmount, 'ether');
	Value = Value1.toString();

  switch (approved) {
	default:console.log("Invalid Token");
	break
	case 'cmp': 
		await CMPContract.methods.approve(uniswapContractAddress, Value).send({from:selectedAccount});
		await DEXContract.methods.swapTokensForTokens(_tokenIn,_tokenOut,Value,0,selectedAccount).send({from:selectedAccount});
	break;

	case 'weth':
		await WETHContract.methods.approve(uniswapContractAddress, Value).send({from:selectedAccount});
		await DEXContract.methods.swapTokensForTokens(_tokenIn,_tokenOut,Value,0,selectedAccount).send({from:selectedAccount});
	break;
  }
	
	console.log("Transaction complete");
  };


